var stringTest = 'daccadfghd_i';

var PairBot = function(string) {
	this.string = string;
	this.underscores = false;
	this.iteration = 0;
	this.pairs = null;
	this.longestPairChar = null;
};

PairBot.prototype = {
	prepare: function(string) {
		var splitString = string.split('');
		newString = removeUnderscores(splitString);
		return newString;
	},
	reset: function() {
		this.pairs = [];
	},
	returnPairs: function(string) {
		var preparedString = this.prepare(string);
		var pairs = findPairs(preparedString);
		return pairs;
	}
};

function findPairs(characters) {
	var pairs = {};
	var pairNums = {};
	var diffCharacters = [];
	// create a subset of all unique characters
	for (var i = 0; i < characters.length; i++) {
		if (diffCharacters.indexOf(characters[i]) === -1) {
			diffCharacters.push(characters[i])
		}
	}

	for (var i = 0; i < characters.length; i++) {
		if (characters.indexOf(diffCharacters[i]) != -1) {
			pairs.letter = diffCharacters[i];
			pairs.distance = characters.indexOf(diffCharacters[i]);
			pairs.positions = [i];

		}
		console.log(pairs);
	}

	return characters;
}

